pragma solidity ^0.5.0;
import "./NewToken.sol";
import "./YallaGiveAdmins.sol";
// TODO : I've set selfdestruct with msg.sender temporary to test 
contract DonationEscrow {
    // should we use wallet contract as a datatype instead of address
    address   public charityOwner;
    address public donator;
    NewToken public token;
    YallaGiveAdmins public YgAdmins;
    uint public donatedValue;
    string public proofOfPayment;
    uint public startTime;
    uint public releaseTime;
    enum State { Pending, Approved, Rejected }
    State public state;

    constructor(address _donator,address  _charityOwner, NewToken _token, YallaGiveAdmins _YgAdmins, uint _donatedValue, string memory _proofOfPayment) public {
        charityOwner = msg.sender;
        donator = _donator;
        charityOwner = _charityOwner;
        token = _token;
        YgAdmins = _YgAdmins;
        donatedValue = _donatedValue;
        proofOfPayment = _proofOfPayment;
        startTime = now;
        releaseTime = now + 1 weeks;
    }

    modifier inState(State _state) {
        require(state == _state, "Invalid state.");
        _;
    }

    modifier onlyCharityOwner() {
        require(msg.sender == charityOwner, "Only token owner can call this");
        _;
    }

    modifier onlyYgAdmin() {
        require (YgAdmins.isYgAdmin(msg.sender), "Only a YallaGive Admin can call this");
        _;
    }

    function validateDonation() public inState(State.Pending) onlyCharityOwner {
        state = State.Approved;
        token.transfer(donator, donatedValue);
        selfdestruct(msg.sender);
    }

    function rejectDonation() public inState(State.Pending) onlyCharityOwner returns(bool) {
        state = State.Rejected;
        releaseTime= now + 1 weeks;
        return true;
    }

    function adminValidation() public inState(State.Rejected) onlyYgAdmin {
        state = State.Approved;
        token.transfer(donator, donatedValue);
        selfdestruct(msg.sender);
    }

    function adminRejection() public inState(State.Rejected) onlyYgAdmin {
        state = State.Rejected;
        token.burn(donatedValue);
        selfdestruct(msg.sender);
    }

    function releaseTokens() public/* inState(State.Pending)*/ {
        require(msg.sender == donator, "Only the donator can call this");
        require(now > releaseTime, "1 week has not passed yet");
        token.transfer(donator, donatedValue);
        selfdestruct(msg.sender);
    }

    // function destroy(address owner) internal returns(bool) {
    //     selfdestruct(owner);
    // }
}
