pragma solidity ^0.5.0;
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Capped.sol";
import 'openzeppelin-solidity/contracts/token/ERC20/ERC20Burnable.sol';

/** we will make it in a sperated contract */
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/utils/ReentrancyGuard.sol";
// import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";

// import "openzeppelin-solidity/contracts/token/ERC20/SafeERC20.sol";
// import "./Ownable.sol";

import './AtaaToken.sol';
import './TokenFactory.sol';
import './YallaGiveAdmins.sol';
import './DonationEscrow.sol';
// import './TokenTrade.sol';

// the contract need to inhert ERC20Capped which internal inhirt ERC20Mintable so you won't do that 
contract NewToken is ERC20Detailed,ERC20Capped, ERC20Burnable, ReentrancyGuard /*, TokenTrade*/ {

       using SafeMath for uint256;
    // using SafeERC20 for IERC20;

    // The token being sold with
     AtaaToken public _attaToken;
   address  public owner;
 YallaGiveAdmins public YgAdmins;
    // Address where funds are collected

    // How many token units a buyer gets per wei.
    // The rate is the conversion between wei and the smallest and indivisible token unit.
    // So, if you are using a rate of 1 with a ERC20Detailed token with 3 decimals called TOK
    // 1 wei will give you 1 unit, or 0.001 TOK.
    uint256 private _rate;


       // uint256 public tokenPrice;      // @param token's price related to AtaaToken.
 constructor(string memory name, string memory symbol, uint8 decimals, uint256 cap, uint256 rate ,address _owner ,  AtaaToken token,YallaGiveAdmins _YgAdmins) 
        ERC20Detailed(name,symbol,decimals) ERC20Capped(cap)    public
        {
              //  tokenPrice = _rate;
         require(rate > 0, " trade contract: rate is 0");
        require(_owner != address(0), " trade contract: wallet is the zero address");
        require(address(token) != address(0), " trade contract: token is the zero address");
        _attaToken=token;
        _rate = rate;
        owner = _owner;
        YgAdmins = _YgAdmins;

    }

    /**
     * @dev low level token purchase ***DO NOT OVERRIDE***
     * This function has a non-reentrancy guard, so it shouldn't be called by
     * another `nonReentrant` function.
     * @param beneficiary Recipient of the token purchase
     */
    function _buyTokens(address beneficiary, uint256 attaAmount) internal nonReentrant  {
        _preValidatePurchase(beneficiary, attaAmount);

        // calculate token amount to be created
        uint256 tokens = _getTokenAmount(attaAmount);

        // update state
        //_weiRaised = _weiRaised.add(attaAmount);

        _processPurchase(beneficiary, tokens);
        // emit TokensPurchased(msg.sender, beneficiary, attaAmount, tokens);


    }
    function _preValidatePurchase(address beneficiary, uint256 amount) internal view {
        require(beneficiary != address(0), "Trade contract: beneficiary is the zero address");
        require(amount != 0, "Trade contract: Amount is 0");
    }

    /**
     * @dev Validation of an executed purchase. Observe state and use revert statements to undo rollback when valid
     * conditions are not met.
     * @param beneficiary Address performing the token purchase
     * @param attaAmount Value in wei involved in the purchase
     */
    function _postValidatePurchase(address beneficiary, uint256 attaAmount) internal view {
        // solhint-disable-previous-line no-empty-blocks
    }

     /**
     * @dev Overrides delivery by minting tokens upon purchase.
     * @param beneficiary Token purchaser
     * @param tokenAmount Number of tokens to be minted
     */
    function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
        // Potentially dangerous assumption about the type of the token.
        require(
            _attaToken.mint(beneficiary, tokenAmount),
                "Minted trade contract: minting failed"
        );
    }
    function _transferAttaTokens( uint256 tokenAmount) internal {
        _attaToken.transfer(owner, tokenAmount);
    }

    /**
     * @dev Executed when a purchase has been validated and is ready to be executed. Doesn't necessarily emit/send
     * tokens.
     * @param beneficiary Address receiving the tokens
     * @param tokenAmount Number of tokens to be purchased
     */
    function _processPurchase(address beneficiary, uint256 tokenAmount) internal {
        _deliverTokens(beneficiary, tokenAmount);
    }

    /**
     * @dev Override for extensions that require an internal state to check for validity (current user contributions,
     * etc.)
     * @param beneficiary Address receiving the tokens
     * @param attaAmount Value in wei involved in the purchase
     */
    function _updatePurchasingState(address beneficiary, uint256 attaAmount) internal {
        // solhint-disable-previous-line no-empty-blocks
    }

    /**
     * @dev Override to extend the way in which ether is converted to tokens.
     * @param attaAmount Value in wei to be converted into tokens
     * @return Number of tokens that can be purchased with the specified _attaAmount
     */
    function _getTokenAmount(uint256 attaAmount) internal view returns (uint256) {
        return attaAmount.mul(_rate);
    }

//     /**
//      * @dev Determines how ETH is stored/forwarded on purchases.
//      */
//     function _forwardFunds() internal {
//         _attaToken.transfer(msg.value);
//     }
      function rate() public view returns (uint256) {
            return _rate;
      }

      function buyWithAttaCoin( uint256 attaAmount) public returns (bool){
      require(_attaToken.allowance(msg.sender, address(this)) >= attaAmount, "More Funds are needed.");
      _buyTokens(msg.sender,  attaAmount);
      _transferAttaTokens(attaAmount);
      return true;

      }
      /**we will assume that the amount that the user sends is true otherwise he might lose his funds.
       if there is any issue it will be handled by the team manually wither by refund or anything else */
      function buyOffChain( uint256 amount , string memory PrOfPayment) public returns (bool){
   // create new escrow 
   //address _donator,address _charityOwner, NewToken _token, YallaGiveAdmins _YgAdmins, uint _donatedValue, string memory _proofOfPayment
   address escrow = address(new DonationEscrow(msg.sender,owner,this,YgAdmins,amount,PrOfPayment));
    _buyTokens(escrow,  amount);
    return true;
   // adjust the amount 
   // send the toekn to escrow


      }

}

// you don't have to implement these function , you can inhirt  ERC20Detailed and the contract will do all te work

