pragma solidity ^0.5.0;

import "openzeppelin-solidity/contracts/access/Roles.sol";

contract YallaGiveAdmins {
  using Roles for Roles.Role;

  event YgAdminAdded(address indexed account);
  event YgAdminRemoved(address indexed account);

  Roles.Role private YgAdmins;

  constructor() public { 
    _addYgAdmin(msg.sender);
  }

  modifier onlyYgAdmin() {
    require(isYgAdmin(msg.sender),"Only admin can call this function");
    _;
  }

  function isYgAdmin(address account) public view returns (bool) {
    return YgAdmins.has(account);
  }

  function addYgAdmin(address account) public onlyYgAdmin   returns (bool){
    _addYgAdmin(account);
    return true;
  }

  function renounceYgAdmin() public {
    _removeYgAdmin(msg.sender);
  }

  function _addYgAdmin(address account) internal {
    YgAdmins.add(account);
    emit YgAdminAdded(account);
  }

  function _removeYgAdmin(address account) internal {
    YgAdmins.remove(account);
    emit YgAdminRemoved(account);
  }
}
